mapper.insert:
INSERT INTO tw_users (
    username, password, nickname, email, phone, avatar, enabled
) VALUES (
    #{username}, #{password}, #{nickname}, #{email}, #{phone}, #{avatar}, #{enabled}
)

mapper.update:
UPDATE tw_users
nickname = #{nickname}, email = #{email}, phone = #{phone}, avatar = #{avatar}, enabled = #{enabled},
WHERE id = #{id}

UPDATE tw_users
SET password = #{password}
WHERE id = #{id}

UPDATE tw_users
SET email_verified = #{emailVerified}
WHERE id = #{id}

mapper.select:
SELECT * FROM tw_users
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}
